define(["underscore"],function(e){var t=function(n,r){if(e.isArray(n)){var i=Math.floor(Math.random()*n.length);return n[i]}if(e.isFunction(n))return n.call(r);if(e.isObject(n)){if(!e.isNumber(n.min)||!e.isNumber(n.max)||n.min>n.max)throw new Error("randomization objects need both a max and a minimum property, also max has to be larger than min");return n.min+(n.max-n.min)*Math.random()}return n};return t});